import React from "react";
import {
  makeStyles,
  createStyles,
  Theme,
  Container,
  Typography,
  TextField,
  Grid,
  InputAdornment,
  Input,
  FormControl
} from "@material-ui/core";
import { Task } from "../../../types/Task";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      height: "2000px",
      padding: theme.spacing(3)
    },
    form: {
      marginTop: theme.spacing(2)
    },
    textField: {
      width: "100%"
    }
  })
);

interface Props {
  task: Task | null;
}

const MainInterface: React.FC<Props> = ({ task }) => {
  const classes = useStyles();

  if (task) {
    if (task.taskDefinitionKey === "recordDamage") {
      return (
        <Container maxWidth="sm" className={classes.root}>
          <Typography variant="h4">{task.name}</Typography>
          <Typography>Bitte fülle das unten stehende Formular aus.</Typography>
          <form className={classes.form} noValidate autoComplete="off">
            <Grid container alignItems="center" justify="center" spacing={2}>
              <Grid item xs={6}>
                <TextField
                  required
                  id="firstname"
                  label="Vorname"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  required
                  id="lastname"
                  label="Nachname"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  required
                  id="address"
                  label="Adresse"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  required
                  id="city"
                  label="Stadt"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  required
                  id="email"
                  label="mailadresse"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  required
                  id="message"
                  label="Ihre Geschichte"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <FormControl required>
                  <Input
                    id="amount"
                    label="Höhe des Schadensfalls"
                    className={classes.textField}
                    startAdornment={
                      <InputAdornment position="start">CHF</InputAdornment>
                    }
                  />
                </FormControl>
              </Grid>
              <Grid item xs={6}></Grid>
              <Grid item xs={6}></Grid>
              <Grid item xs={6}></Grid>
              <Grid item xs={6}></Grid>
              <Grid item xs={6}></Grid>
            </Grid>
          </form>
        </Container>
      );
    } else {
      return (
        <Container maxWidth="sm" className={classes.root}>
          <Typography variant="h4">{task.name}</Typography>
          <Typography>Bitte wähle einen Task aus der Liste links.</Typography>
        </Container>
      );
    }
  } else {
    return (
      <Container maxWidth="sm" className={classes.root}>
        <Typography variant="h4">Kein Task ausgewählt</Typography>
        <Typography>Bitte wähle einen Task aus der Liste links.</Typography>
      </Container>
    );
  }
};

export default MainInterface;
