import React from "react";
import {
  makeStyles,
  createStyles,
  Theme,
  Container,
  Typography,
  TextField,
  Grid
} from "@material-ui/core";
import { Task } from "../../../types/Task";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      height: "2000px",
      padding: theme.spacing(3)
    },
    form: {
      marginTop: theme.spacing(2)
    },
    textField: {
      width: "100%"
    }
  })
);

interface Props {
  task: Task | null;
}

const MainInterface: React.FC<Props> = ({ task }) => {
  const classes = useStyles();

  if (task) {
    if (task.taskDefinitionKey === "recordDamage") {
      return (
        <Container maxWidth="sm" className={classes.root}>
          <Typography variant="h4">{task.name}</Typography>
          <Typography>Bitte f√ºlle das unten stehende Formular aus.</Typography>
          <form className={classes.form} noValidate autoComplete="off">
            <Grid container alignItems="center" justify="center" spacing={2}>
              <Grid item xs={6}>
                <TextField
                  required
                  id="firstname"
                  label="Vorname"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  required
                  id="lastname"
                  label="Nachname"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  required
                  id="lastname"
                  label="Nachname"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}></Grid>
              <Grid item xs={6}></Grid>
              <Grid item xs={6}></Grid>
            </Grid>
          </form>
        </Container>
      );
    } else {
      return (
        <Container maxWidth="sm" className={classes.root}>
          <Typography variant="h4">{task.name}</Typography>
          <Typography>Bitte w√§hle einen Task aus der Liste links.</Typography>
        </Container>
      );
    }
  } else {
    return (
      <Container maxWidth="sm" className={classes.root}>
        <Typography variant="h4">Kein Task ausgew√§hlt</Typography>
        <Typography>Bitte w√§hle einen Task aus der Liste links.</Typography>
      </Container>
    );
  }
};

export default MainInterface;
