import {
  Button,
  createStyles,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListSubheader,
  makeStyles,
  Theme
} from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import React from "react";
import TaskService from "../../../services/taskService";
import { Task } from "../../../types/Task";

const drawerWidth = 240;

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    drawer: {
      width: drawerWidth,
      flexShrink: 0
    },
    drawerPaper: {
      width: drawerWidth
    },
    content: {
      flexGrow: 1,
      padding: theme.spacing(3)
    },
    startButton: {
      width: "fit-content",
      margin: theme.spacing(1, 2)
    },
    toolbar: { ...theme.mixins.toolbar }
  })
);

interface Props {
  tasks: Task[];
  addedTask: (task: Task) => Promise<void>;
  selectedTask: (task: Task) => void;
}

const Sidebar: React.FC<Props> = ({ addedTask, selectedTask, tasks }) => {
  const classes = useStyles();
  const taskService: TaskService = new TaskService();

  const handleAddTask = async () => {
    const res = await taskService.start();
    addedTask(res);
  };

  return (
    <div>
      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <div className={classes.toolbar} />
        <List dense>
          <ListSubheader>Offene Tasks</ListSubheader>
          {tasks.map(
            (task: Task) =>
              task.taskDefinitionKey === "SayHello" && (
                <ListItem
                  button
                  key={task.id}
                  onClick={() => selectedTask(task)}
                >
                  <ListItemText primary={task.name} />
                </ListItem>
              )
          )}
        </List>
        <Button
          variant="contained"
          color="primary"
          onClick={() => handleAddTask()}
          className={classes.startButton}
          startIcon={<AddIcon />}
        >
          Neuer Task
        </Button>
        <List dense>
          <ListSubheader>Abgeschlossene Tasks</ListSubheader>
          {tasks.map(
            (task: Task) =>
              task.taskDefinitionKey === "SayGoodbye" && (
                <ListItem
                  button
                  key={task.id}
                  onClick={() => selectedTask(task)}
                >
                  <ListItemText primary={task.name} />
                </ListItem>
              )
          )}
        </List>
      </Drawer>
    </div>
  );
};

export default Sidebar;
