import {
  Container,
  createStyles,
  FormControl,
  Grid,
  Input,
  InputAdornment,
  InputLabel,
  makeStyles,
  MenuItem,
  Select,
  TextField,
  Theme,
  Typography,
  Button
} from "@material-ui/core";
import React from "react";
import { Task } from "../../../types/Task";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      height: "2000px",
      padding: theme.spacing(3)
    },
    form: {
      marginTop: theme.spacing(2)
    },
    textField: {
      width: "100%"
    }
  })
);

interface Props {
  task: Task | null;
}

const MainInterface: React.FC<Props> = ({ task }) => {
  const classes = useStyles();

  if (task) {
    if (task.taskDefinitionKey === "recordDamage") {
      return (
        <Container maxWidth="sm" className={classes.root}>
          <Typography variant="h4">{task.name}</Typography>
          <Typography>Bitte fülle das nachfolgende Formular aus.</Typography>
          <form className={classes.form} noValidate autoComplete="off">
            <Grid container alignItems="center" justify="center" spacing={2}>
              <Grid item xs={6}>
                <TextField
                  id="firstname"
                  label="Vorname"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="lastname"
                  label="Nachname"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="address"
                  label="Adresse"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="city"
                  label="Stadt"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="email"
                  label="Mailadresse"
                  className={classes.textField}
                />
              </Grid>
              <Grid item xs={6}>
                <FormControl className={classes.textField}>
                  <InputLabel htmlFor="amount">
                    Höhe des Schadensfalls
                  </InputLabel>
                  <Input
                    id="amount"
                    startAdornment={
                      <InputAdornment position="start">CHF</InputAdornment>
                    }
                  />
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <FormControl className={classes.textField}>
                  <InputLabel id="insurance-label">
                    Welche Versicherung haben Sie abgeschlossen?
                  </InputLabel>
                  <Select labelId="insurance-label" id="insurance">
                    <MenuItem value={"bronze"}>Bronze</MenuItem>
                    <MenuItem value={"silver"}>Silber</MenuItem>
                    <MenuItem value={"gold"}>Gold</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  id="message"
                  label="Ihre Geschichte"
                  className={classes.textField}
                  multiline
                />
              </Grid>
              <Grid item xs={12} justify="flex-end">
                <Button variant="contained">Erfassen</Button>
              </Grid>
            </Grid>
          </form>
        </Container>
      );
    } else {
      return (
        <Container maxWidth="sm" className={classes.root}>
          <Typography variant="h4">{task.name}</Typography>
          <Typography>Bitte wähle einen Task aus der Liste links.</Typography>
        </Container>
      );
    }
  } else {
    return (
      <Container maxWidth="sm" className={classes.root}>
        <Typography variant="h4">Kein Task ausgewählt</Typography>
        <Typography>Bitte wähle einen Task aus der Liste links.</Typography>
      </Container>
    );
  }
};

export default MainInterface;
